import{_ as c,a6 as e,L as d,M as h,W as t,a7 as o,aa as p,h as b}from"./chunks/framework.DSr6HixA.js";const U=JSON.parse('{"title":"安装指南","description":"详细的 ShiroSU 安装步骤和配置说明","frontmatter":{"layout":"doc","title":"安装指南","description":"详细的 ShiroSU 安装步骤和配置说明","outline":"deep","footer":false,"breadcrumbs":[{"title":"ShiroSU","link":"/zh"},{"title":"base","link":""},{"title":"安装指南","link":"/zh/base/install"}]},"headers":[],"relativePath":"base/install.md","filePath":"zh/base/install.md","lastUpdated":1755655878000}'),_={name:"base/install.md"};function g(u,a,S,m,k,f){const r=e("ArticleMetadata"),l=e("NolebasePageProperties"),i=e("Downloaded"),s=e("NolebaseGitContributors"),n=e("NolebaseGitChangelog");return h(),d("div",null,[a[0]||(a[0]=t("h1",{id:"下载-shirosu-管理器",tabindex:"-1"},[b("下载 ShiroSU 管理器 "),t("a",{class:"header-anchor",href:"#下载-shirosu-管理器","aria-label":"Permalink to “下载 ShiroSU 管理器”"},"​")],-1)),o(r),o(l),o(i),a[1]||(a[1]=p('<h2 id="获取-root-权限" tabindex="-1">获取 Root 权限 <a class="header-anchor" href="#获取-root-权限" aria-label="Permalink to “获取 Root 权限”">​</a></h2><h3 id="通过其他管理器授权" tabindex="-1">通过其他管理器授权 <a class="header-anchor" href="#通过其他管理器授权" aria-label="Permalink to “通过其他管理器授权”">​</a></h3><p>将 <code>ShiroSU</code> 作为模块管理器使用时，需要预先安装 <code>Magisk</code> 或 <code>KernelSU</code> 等 Root 方案。</p><ol><li>在 <code>Magisk</code> 或 <code>KernelSU</code> 管理器中，授予 <code>ShiroSU</code> 应用 Root 权限。</li><li>授权后，<code>ShiroSU</code> 将自动识别并加载模块列表，以进行后续管理。</li></ol><h3 id="通过-shirosu-开发中" tabindex="-1">通过 ShiroSU （开发中） <a class="header-anchor" href="#通过-shirosu-开发中" aria-label="Permalink to “通过 ShiroSU （开发中）”">​</a></h3><div class="note custom-block github-alert"><p class="custom-block-title">NOTE</p><p><code>ShiroSU</code> 的原生 Root 实现功能正在开发中，当前版本暂不支持。</p></div><hr><h2 id="模块管理器使用说明" tabindex="-1">模块管理器使用说明 <a class="header-anchor" href="#模块管理器使用说明" aria-label="Permalink to “模块管理器使用说明”">​</a></h2><p>模块管理界面提供了对所有已安装模块的集中控制，支持搜索、排序、启用、禁用及卸载等核心功能。</p><h3 id="_1-模块列表全局操作" tabindex="-1">1. 模块列表全局操作 <a class="header-anchor" href="#_1-模块列表全局操作" aria-label="Permalink to “1. 模块列表全局操作”">​</a></h3><p>位于页面右上角，提供针对所有模块的一键式批量处理。</p><ul><li><strong>启用所有</strong>：激活列表内的全部模块。</li><li><strong>禁用所有</strong>：禁用列表内的全部模块。</li><li><strong>更新所有</strong>: 检查并更新所有模块到最新版本。</li></ul><h3 id="_2-单个模块精细化操作" tabindex="-1">2. 单个模块精细化操作 <a class="header-anchor" href="#_2-单个模块精细化操作" aria-label="Permalink to “2. 单个模块精细化操作”">​</a></h3><p>每个模块以独立的卡片形式展示，并支持以下交互：</p><ul><li><p><strong>状态切换</strong> 通过卡片上的开关控制模块的启用或禁用。禁用后，模块卡片背景将变为灰色。</p></li><li><p><strong>卸载模块</strong> 从模块列表中移除指定模块。操作完成后，该模块卡片将显示删除线样式。</p></li><li><p><strong>收藏与置顶</strong> 通过 <strong>长按</strong> 模块卡片，可将其收藏并固定于列表顶部，方便快速访问。</p></li><li><p><strong>专属功能访问</strong></p><ul><li><strong>“界面”按钮</strong>：启动模块内置的 WebUI。</li><li><strong>“功能”按钮</strong>：执行模块的 <code>action.sh</code> 脚本。</li></ul></li></ul><h3 id="_3-命令集扩展模块" tabindex="-1">3. 命令集扩展模块 <a class="header-anchor" href="#_3-命令集扩展模块" aria-label="Permalink to “3. 命令集扩展模块”">​</a></h3><p>系统支持通过安装独立模块来加载额外的命令行工具集，以增强终端功能。</p><ul><li><strong>可用扩展模块</strong>：<code>coreutils</code>、<code>busybox</code>、<code>sush</code></li></ul><div class="warning custom-block github-alert"><p class="custom-block-title">WARNING</p><p>上述扩展模块未来计划直接集成至 <code>ShiroSU</code> 主程序中。</p></div><hr>',20)),o(s),o(n)])}const N=c(_,[["render",g]]);export{U as __pageData,N as default};
